import React, { Component } from "react";
import axios from "axios";

class CelularDetails extends Component {
  state = {
    celulares: "",
  };

  componentDidMount = async () => {
    try {
      const response = await axios.get(
        `https://phones--melhorcom.repl.co/phone/${this.props.match.params.id}`
      );
      console.log(response);
      this.setState({ character: { ...response.data } });
    } catch (err) {
      console.log(err);
    }
  };

  render() {
    return (
      <div className="container d-flex flex-column">
        <h1>{this.state.character.name} Details</h1>
        <span>
          <strong>ID: </strong>
          {this.state.character.id}
        </span>
        <span>
          <strong>Name: </strong>
          {this.state.character.name}
        </span>
        <span>
          <strong>Occupation: </strong>
          {this.state.character.occupation}
        </span>
        <span>
          <strong>Weapon: </strong>
          {this.state.character.weapon}
        </span>
        <span>
          <strong>Is in debt? </strong>
          {this.state.character.debt ? "Yes" : "No"}
        </span>
      </div>
    );
  }
}

export default CelularDetails;
